      SUBROUTINE STRIP(STR,N)
      CHARACTER*(*) STR
      INTEGER N, I
      N = LEN(STR)
      DO I = LEN(STR), 1, -1
        IF(STR(I:I) .NE. ' ') THEN
          N = I
          RETURN
        ENDIF
      END DO
      N = 0
      RETURN
      END

