      SUBROUTINE PLCLOSE
      INCLUDE 'AVL.INC'
      COMMON /AVLSTUB/ NDEFINI, NMASINI, NHEAP, NPLINIT,
     &  NINPUT, NPARSET, NENCALC, NVARINI, NPLPARS,
     &  NMASGET, NRUNGET, NRUNINI, NPLCLOSE
      IF(NDEFINI.EQ.0) NDEFINI = 1
      IF(NMASINI.EQ.0) NMASINI = 1
      IF(NHEAP.EQ.0) NHEAP = 1
      IF(NPLINIT.EQ.0) NPLINIT = 1
      NPLCLOSE = NPLCLOSE + 1
      WRITE(*,*) VERSION, PI, DTR, LUINP, LURUN, LUOUT, LUSTD, LUSYS,
     &  RMASS0, NDEFINI, NMASINI, NHEAP, NPLINIT, NPLCLOSE
      RETURN
      END

